import React from 'react';

const Form = ({newSpacecraft, setNewSpacecraft}) => {

  const handleChange = e => {
    setNewSpacecraft({
      ...newSpacecraft,
      [e.target.name]: e.target.value
    })
  }

  let { name, type, activity, origin, tripulation, combustible, state, weight } = newSpacecraft

  const handleSubmit = () => {
    tripulation = parseInt(tripulation, 10);
    //validate the data
    if(name === '' || type === '' || activity === '' || origin === '' || tripulation < 0 || combustible === '' || state === '' || weight ==='' ) {
      alert('Todos los campos son oblogatorios')
      return
    }

    //query
    const requestInit = {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(newSpacecraft)
    }
    fetch('http://localhost:8080/api/v1/spacecraft', requestInit)
        .then(res => res.json())
        .then(res => console.log(res))

    // restarting newSpacecraftState
    setNewSpacecraft({
      name: '',
    type: '',
    activity: '',
    origin: '',
    tripulation: 0,
    combustible: '',
    state: '',
    weight: ''
    })



  }

  return (
    <form onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="name" className="form-label">Nombre</label>
        <input name="name" onChange={handleChange} type="text" id="name" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="type" className="form-label">Tipo</label>
        <input name="type" onChange={handleChange} type="text" id="type" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="activity" className="form-label">Actividad</label>
        <input name="activity" onChange={handleChange} type="text" id="activity" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="origin" className="form-label">Origen</label>
        <input name="origin" onChange={handleChange} type="text" id="origin" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="tripulation" className="form-label">Tripulacion</label>
        <input name="tripulation" onChange={handleChange} type="number" id="tripulation" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="combustible" className="form-label">Combustible</label>
        <input name="combustible" onChange={handleChange} type="text" id="combustible" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="state" className="form-label">Estado</label>
        <input name="state" onChange={handleChange} type="text" id="state" className="form-control"/>
      </div>
      <div className="mb-3">
        <label htmlFor="weight" className="form-label">Peso</label>
        <input name="weight" onChange={handleChange} type="text" id="weight" className="form-control"/>
      </div>
      <button type="submit" className="btn btn-primary">Submit</button>
    </form>
   );
}

export default Form;
