import React, { Fragment, useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import SpaceList from "./components/SpaceList";
import Form from "./components/Form";
// import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";

function App() {

  const [newSpacecraft, setNewSpacecraft] = useState({
    name: ' ',
    activity: ' ',
    origin: ' ',
    tripulation: 0,
    combustible: ' ',
    state: ' ',
    weight: ' ',
    burden: ' ',
    thrust: ' ',
    height: ' ',
    power: ' ',
    objectOfStudy: ' ',
    inOrbit: ' ',
    speed: ' ',
    mission: ' '
  })

  const [spacecrafts, setSpacecrafts] = useState([])
  const [tabSpacecrafts, setTableSpacecrafts] = useState([])

  const [listUpdated, setListUpdated] = useState(false)

  const [search, setSearch]= useState("");

  useEffect(() => {
    const getSpacecrafts = () => {
      fetch('http://localhost:8080/api/v1/spacecraft')
        .then(res => res.json())
        .then(res => setSpacecrafts(res))
        .then(res => setTableSpacecrafts(res))
    }
    getSpacecrafts()
    setListUpdated(false)
  }, [listUpdated])

  const handleChange=e=>{
    setSearch(e.target.value);
    filter(e.target.value);
  }

  const filter=(wordSearch)=>{
    var resultSearch = spacecrafts.filter((element) => {
      if(element.name.toString().toLoweCase().includes(wordSearch.toLoweCase())){
        return element;
      }
    })
  }

  return (
    <Fragment>
      <Navbar brand="Estacion Espacial" />
      <div className="containerInput">
        <input
          className="form-control inputBuscar"
          value={search}
          placeholder="Busqueda por caracteristicas de la nave"
          onChange={handleChange}
        />
        <button className="btn btn-succes">
          <FontAwesomeIcon icon={faSearch} />
        </button>
      </div>
      <div className="container ms-0">
        <div className="row" style={{float: 'center'}}>
          <div className="col">
            <h2 style={{textAlign: 'center'}}>Spacecraft List</h2>
            <SpaceList spacecrafts={spacecrafts} listUpdated={listUpdated} setListUpdated={setListUpdated} newSpacecraft={newSpacecraft} setNewSpacecraft={setNewSpacecraft}/>
          </div>
        <div/>
        <div className="row"></div>
          <div className="col">
            <h2 style={{textAlign: 'center'}}>Spacecraft Form</h2>
            <Form newSpacecraft={newSpacecraft} setNewSpacecraft={setNewSpacecraft} />
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
