import React, { Fragment, useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import SpaceList from "./components/SpaceList";
import Form from "./components/Form";

function App() {

  const [newSpacecraft, setNewSpacecraft] = useState({
    name: 'empty',
    activity: 'empty',
    origin: 'empty',
    tripulation: 0,
    combustible: 'empty',
    state: 'empty',
    weight: 'empty',
    burden: 'empty',
    thrust: 'empty',
    height: 'empty',
    power: 'empty',
    objectOfStudy: 'empty',
    inOrbit: 'empty',
    speed: 'empty',
    mission: 'empty'
  })

  const [spacecrafts, setSpacecrafts] = useState([])

  useEffect(() => {
    const getSpacecrafts = () => {
      fetch('http://localhost:8080/api/v1/spacecraft')
        .then(res => res.json())
        .then(res => setSpacecrafts(res))
    }
    getSpacecrafts()
  }, [])

  return (
    <Fragment>
      <Navbar brand="Estacion Espacial" />
      <div className="container ms-0">
        <div className="row" style={{float: 'center'}}>
          <div className="col">
            <h2 style={{textAlign: 'center'}}>Spacecraft List</h2>
            <SpaceList spacecrafts={spacecrafts}/>
          </div>
        <div/>
        <div className="row"></div>
          <div className="col">
            <h2 style={{textAlign: 'center'}}>Spacecraft Form</h2>
            <Form newSpacecraft={newSpacecraft} setNewSpacecraft={setNewSpacecraft} />
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
